source('C:/rprojects/hasznaltauto/R/functions.R')
library(parallel)

n_pages <- SleepyRead("https://www.hasznaltauto.") %>% 
  html_nodes(".last a") %>% 
  html_text() %>% 
  first() %>% 
  as.numeric()


url_to_lists <-  str_c(
  "https://www.hasznaltauto.", 1:n_pages
)

cl <- makeCluster(7)
clusterEvalQ(cl, library(rvest))
clusterEvalQ(cl, library(tidyverse))
clusterExport(cl, list("SafelyRead", "url_to_lists", "GetURL", "GetHTMLText"), envir = environment())

available_cars <- parLapply(cl, url_to_lists, function(url) {
  SafelyRead(url) %>% 
    {tibble(url_to_car = GetURL(., ".cim-kontener a"), price = GetHTMLText(., ".vetelar"))}
})

stopCluster(cl)

available_cars <- available_cars %>% 
  reduce(rbind) %>% 
  na.omit()

cars_yesterday <- list.files("C:/rprojects/hasznaltauto/data/available_cars/") %>%
  str_remove_all("available_cars_") %>% 
  str_remove_all(".RDS") %>% 
  as.Date() %>% 
  sort() %>% 
  keep(~ . != Sys.Date()) %>% 
  last() %>% 
  {read_rds(str_c("C:/rprojects/hasznaltauto/data/available_cars/available_cars_", ., ".RDS"))} %>% 
  select(url_to_car)

cars_yesterday <- list.files("C:/rprojects/hasznaltauto/data/available_cars/") %>% 
  map(~ read_rds(str_c("C:/rprojects/hasznaltauto/data/available_cars/", .))) %>% 
  reduce(rbind) %>% 
  select(url_to_car) %>% 
  unique()

write_rds(available_cars, file = str_c("C:/rprojects/hasznaltauto/data/available_cars/available_cars_", Sys.Date(), ".RDS"))

available_cars %>% 
  anti_join(cars_yesterday) %>% 
  nrow() %>% 
  {print(paste("Scrape", ., "new cars!"))}

cars_data <- available_cars %>% 
  anti_join(cars_yesterday) %>%
  mutate(
    page = map(url_to_car, SleepyRead)
  ) %>% 
  filter(map_lgl(page, ~ !is.na(.))) %>% 
  mutate(
    data = map(page, html_table, fill = TRUE),
    other_data = map(page, GetHTMLText, "#adatlap li"),
    description = map(page, GetHTMLText, ".leiras div"),
    contact = map(page, GetHTMLText, ".contact-button-text"),
  ) %>% 
  select(url_to_car, data, other_data, description, contact)

write_rds(cars_data, file = str_c("C:/rprojects/hasznaltauto/data/cars_data/cars_data", Sys.Date(), ".RDS"))


